package BJ1008;
// A/B

import java.util.Scanner;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		Scanner sc = new Scanner(System.in);

		int A = sc.nextInt();
		int B = sc.nextInt();
		
		double res = (double)A / B;
		
		System.out.println(res);
	}

}
